    private void result1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_result1ActionPerformed
    String jtxt1 = txt1.getText();
    String jtxt2 = txt2.getText();
    
    int n1 = Integer.parseInt(jtxt1);
    int n2 = Integer.parseInt(jtxt2);
    
    int res = n1+n2;
    
    String resultado = String.valueOf(res);
    result.setText(resultado);
    }//GEN-LAST:event_result1ActionPerformed

